import { Hono } from "hono";
import { cors } from "hono/cors";
import { db } from "@acme/database";

const app = new Hono();

app.use("/*", cors());

app.get("/products", async (c) => {
  const products = await db.product.findMany();
  return c.json(products);
});

app.get("/products/:id", async (c) => {
  const product = await db.product.findUnique({ where: { id: c.req.param("id") } });
  return c.json(product);
});

app.post("/orders", async (c) => {
  const { items } = await c.req.json();
  const order = await db.order.create({ data: { items } });
  return c.json(order);
});

export default app;

import { Button } from "@acme/ui";
import { api } from "@acme/api-client";

export default async function Home() {
  const products = await api.products.list();

  return (
    <main>
      <h1>Acme Store</h1>
      <div className="grid">
        {products.map((p) => (
          <div key={p.id}>
            <h2>{p.name}</h2>
            <p>{p.price}</p>
            <Button>Add to Cart</Button>
          </div>
        ))}
      </div>
    </main>
  );
}

import { ReactNode } from "react";

export default function RootLayout({ children }: { children: ReactNode }) {
  return (
    <html>
      <body>{children}</body>
    </html>
  );
}

export { Button } from "./button";
export { Card } from "./card";
export { Input } from "./input";

// Mock database client (would be Prisma in real app)
export const db = {
  product: {
    async findMany() {
      return [
        { id: "1", name: "Widget", price: 9.99 },
        { id: "2", name: "Gadget", price: 19.99 },
      ];
    },
    async findUnique({ where }: { where: { id: string } }) {
      return { id: where.id, name: "Widget", price: 9.99 };
    },
  },
  order: {
    async create({ data }: { data: unknown }) {
      return { id: "order-1", ...data };
    },
  },
};

const API_URL = process.env.API_URL || "http://localhost:3001";

export const api = {
  products: {
    async list() {
      const res = await fetch(`${API_URL}/products`);
      return res.json();
    },
    async get(id: string) {
      const res = await fetch(`${API_URL}/products/${id}`);
      return res.json();
    },
  },
  orders: {
    async create(items: { productId: string; quantity: number }[]) {
      const res = await fetch(`${API_URL}/orders`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ items }),
      });
      return res.json();
    },
  },
};
